var requestTimeout = 500;
var multiParams = false;

self.onInit = function() {
    var subscription = self.ctx.defaultSubscription;
    var utils = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('utils'));
    var rpcEnabled = subscription.rpcEnabled;
    var deviceName = 'Simulated';
    var prompt;
    if (subscription.targetDeviceName && subscription.targetDeviceName.length) {
        deviceName = subscription.targetDeviceName;
    }
    if (self.ctx.settings.requestTimeout) {
        requestTimeout = self.ctx.settings.requestTimeout;
    }
    if (self.ctx.settings.multiParams) {
        multiParams = self.ctx.settings.multiParams;
    }
    var greetings = 'Welcome to ThingsBoard RPC debug terminal.\n\n';
    if (!rpcEnabled) {
        greetings += 'Target device is not set!\n\n';
        prompt = '';
    } else {
        greetings += 'Current target device for RPC commands: [[b;#fff;]' + deviceName + ']\n\n';
        greetings += 'Please type [[b;#fff;]\'help\'] to see usage.\n';
        prompt = '[[b;#8bc34a;]' + deviceName +']> ';
    }

    var terminal = $('#device-terminal', self.ctx.$container).terminal(
        function(command) {
            if (command !== '') {
                try {
                    var localCommand = command.trim();
                    var requestUUID = utils.guid();
                    if (localCommand === 'help') {
                        printUsage(this);
                    } else {
                        var cmdObj = $.terminal.parse_command(localCommand);
                        if (cmdObj.args) {
                            if (!multiParams && cmdObj.args.length > 1) {
                                this.error("Wrong number of arguments!");
                                this.echo(' ');
                            }
                            else {
                                if (cmdObj.args.length) {
                                    var params = getMultiParams(cmdObj.args);
                                }
                                performRpc(this, cmdObj.name, params, requestUUID);
                            }
                        }
                    }
                } catch(e) {
                    this.error(new String(e));
                }
            } else {
               this.echo('');
            }
        }, {
            greetings: greetings,
            prompt: prompt,
            enabled: rpcEnabled
    });



    if (!rpcEnabled) {
        terminal.error('No RPC target detected!').pause();
    }
}


function printUsage(terminal) {
    var commandsListText = '\n[[b;#fff;]Usage:]\n';
    commandsListText += '   <method> [params body]]\n\n';
    commandsListText += '[[b;#fff;]Example 1  (multiParams===false):]\n'; 
    commandsListText += '   myRemoteMethod1 myText\n\n'; 
    commandsListText += '[[b;#fff;]Example 2  (multiParams===false):]\n'; 
    commandsListText += '   myOtherRemoteMethod "{\\"key1\\":2,\\"key2\\":\\"myVal\\"}"\n\n'; 
    commandsListText += '[[b;#fff;]Example 3 (multiParams===true)]\n'; 
    commandsListText += '   <method> [params body] = "all the string after the method, including spaces"]\n';
    commandsListText += '   myOtherRemoteMethod "{\\"key1\\": "battery level", \\"key2\\": \\"myVal\\"}"\n'; 
    terminal.echo(new String(commandsListText));
}

function performRpc(terminal, method, params, requestUUID) {
    terminal.pause();
    self.ctx.controlApi.sendTwoWayCommand(method, params, requestTimeout, requestUUID).subscribe(
        function success(responseBody) {
            terminal.echo(JSON.stringify(responseBody));
            terminal.echo(' ');
            terminal.resume();
        },
        function fail() {
            var errorText = self.ctx.defaultSubscription.rpcErrorText;
            terminal.error(errorText);
            terminal.echo(' ');
            terminal.resume();
        }
    );
}

function getMultiParams(cmdObj) {
    var params = "";
    cmdObj.forEach((element) => {
        try {
            params += " " + JSON.strigify(JSON.parse(element));
        } catch (e) {
            params += " " + element;
        }
    })
    return params.trim();
}


self.onDestroy = function() {
}

