self.onInit = function() {
    self.ctx.ngZone.run(function() {
       init(); 
       self.ctx.detectChanges();
    });
};

function init() {
    let rpcEnabled = self.ctx.defaultSubscription.rpcEnabled;

    self.ctx.$scope.buttonLable = self.ctx.settings.buttonText;
    self.ctx.$scope.showTitle = self.ctx.settings.title &&
        self.ctx.settings.title.length ? true : false;
    self.ctx.$scope.title = self.ctx.settings.title;
    self.ctx.$scope.styleButton = self.ctx.settings.styleButton;

    if (self.ctx.settings.styleButton.isPrimary ===
        false) {
        self.ctx.$scope.customStyle = {
            'background-color': self.ctx.$scope.styleButton.bgColor,
            'color': self.ctx.$scope.styleButton.textColor
        };
    }

    if (!rpcEnabled) {
        self.ctx.$scope.error =
            'Target device is not set!';
    }

    self.ctx.$scope.sendCommand = function() {
        var rpcMethod = self.ctx.settings.methodName;
        var rpcParams = self.ctx.settings.methodParams;
        var timeout = self.ctx.settings.requestTimeout;
        var oneWayElseTwoWay = self.ctx.settings.oneWayElseTwoWay ?
            true : false;

        var commandPromise;
        if (oneWayElseTwoWay) {
            commandPromise = self.ctx.controlApi.sendOneWayCommand(
                rpcMethod, rpcParams, timeout);
        } else {
            commandPromise = self.ctx.controlApi.sendTwoWayCommand(
                rpcMethod, rpcParams, timeout);
        }
        commandPromise.subscribe(
            function success() {
                self.ctx.$scope.error = "";
                self.ctx.detectChanges();
            },
            function fail(rejection) {
                if (self.ctx.settings.showError) {
                    self.ctx.$scope.error =
                        rejection.status + ": " +
                        rejection.statusText;
                    self.ctx.detectChanges();
                }
            }
        );
    };
}

